import React, { useState, useEffect } from 'react'
import Home from '../Home/Home'
import "./Shop.scss"
export default function Shop() {
    const [offcanvas, swtOffcanvas] = useState(false)
    const [isLoading, setIsLoading] = useState(true);
    function bootstrapBnt(){
       let cur =  sessionStorage.getItem("shop")
       if(cur != ""){
        swtOffcanvas(cur)
       }
    }
    useEffect(() => {
        bootstrapBnt()
    }, [])
    useEffect(() => {
        let sorts = localStorage.getItem("sort")
        if (sorts != "") {
            setSelectVal(sorts)
        }
        else {
            setSelectVal("choose")
        }
        setIsLoading(false)
        const delay = 1000;
        <Delay />
        const redirectTimeout = setTimeout(() => {
            setIsLoading(true)
        }, delay);

        return () => {
            clearTimeout(redirectTimeout);
        };
    }, [])
    console.log(offcanvas);
    return (
        <div>
            <Home/>
            <div className={`${offcanvas ? "active-offcanvas" : ""} offcanvass`}>
                fdsdfsdfsdfdsfsdfsdfsdfdsf
            </div>
        </div>
    )
}
