import React, { useEffect, useState } from 'react'
import { NavLink } from 'react-router-dom'
import { useNavigate } from 'react-router-dom'
import Delay from '../Delay/Delay';
import "./Sitebar.scss"
export default function SiteBar() {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true)
  const [activeLink, setactiveLink] = useState(false)

  const [sitebar, setSitebar] = useState([
    {
      icon: "bi bi-list",
      id: 1,
      path: "/history",
    },
    {
      icon: "bi bi-house",
      id: 2,
      path: "/home",
    },
    {
      icon: "bi bi-gear",
      id: 3,
      path: "/setting",
    },
    {
      icon: "bi bi-pie-chart",
      id: 4,
      path: "/piechard",
    },
    {
      icon: "bi bi-chat-square",
      id: 5,
      path: "/chat",
    },
    {
      icon: "bi bi-cart3",
      id: 6,
      path: "/shop",
    },
    {
      icon: "bi bi-person",
      id: 7,
      path: "/login",
    },
  ])
  const [sitebarMob, setSitebarMob] = useState([
    {
      icon: "bi bi-list",
      id: 1,
      path: "/history",
    },
    {
      icon: "bi bi-pie-chart",
      id: 2,
      path: "/piechard",
    },
    {
      icon: "bi bi-house",
      id: 3,
      path: "/home",
    },
    {
      icon: "bi bi-cart3",
      id: 6,
      path: "/shop",
    },
    {
      icon: "bi bi-person",
      id: 7,
      path: "/login",
    },
  ])
  function delay(evt) {
    evt.
    setactiveLink(true)
  }

  console.log(activeLink);
  useEffect(()=>{
    setIsLoading(false)
    const delay = 1000;
    <Delay />
    const redirectTimeout = setTimeout(() => {
      setIsLoading(true)
    }, delay);

    return () => {
      clearTimeout(redirectTimeout);
    };
  },[])

  return (
    <div>
      {!isLoading ? <Delay /> : <div className='row siteBar Decktop'>
        {
          sitebar?.map((iteam, index) => {
            return (
              <div key={index} className={`${activeLink ? "active-link" : ""} col-2  col-sm-12`}>
                <div>
                  <NavLink to={`${isLoading ? iteam.path : ""}`} onClick={() => delay()} className={`${({ isActive, isPending }) => isPending ? "pending" : isActive ? `active` : ``} `}>
                    <i className={iteam.icon}></i>
                  </NavLink>
                </div>
              </div>
            )
          })
        }
      </div>}

      {!isLoading ? <Delay /> : <div className='row siteBar Modile'>
        {
          sitebarMob?.map((iteam, index) => {
            return (
              <div key={index} className="col-2 col-sm-12">
                <div>
                  <NavLink onClick={() => delay(iteam.path)} className={`${({ isActive, isPending }) => isPending ? "pending" : isActive ? "active" : "text-white link"} `}>
                    <i className={iteam.icon}></i>
                  </NavLink>
                </div>
              </div>
            )
          })
        }
      </div>}


    </div>
  )
}
