import React, { useState, useEffect } from 'react'
import { useNavigate } from 'react-router-dom'
import "./Register.scss"
import BacdropImage from '../../Assets/Images/photo_2023-10-19_07-28-40.jpg'
import { NavLink } from 'react-router-dom'
import Delay from '../Delay/Delay'
export default function Register() {
    const navigate = useNavigate();
    const [isLoading, setIsLoading] = useState(true);
    const [phoneNumber, setPhonenNumber] = useState("")
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")
    const pattern = /^\d+$/;
   
    const postLoginData = async () => {
        ///////////
    }
    function handleKeyDown(e){
        const allowedCharacters = /[0-9+]/;
        const keyPressed = e.key;
    }
    console.log(pattern.test(phoneNumber));


    function delayLink(page) {
        setIsLoading(false)
        console.log(page);
        const delay = 1000;
        <Delay/>
        const redirectTimeout = setTimeout(() => {
            setIsLoading(true)
            navigate(page);
        }, delay);

        return () => {
            clearTimeout(redirectTimeout);
        };
    }

    return (
        <div>
            {(!isLoading) ? <Delay/> : <>
            <img className='login-image' src={BacdropImage} alt="LoginImage" />
            <div>
                <section>
                    <div className="form-box">
                        <div className="form-value">
                            <form onSubmit={postLoginData}>
                                <h2>Register</h2>
                                <div className="inputbox">
                                    <input onKeyDown={handleKeyDown} value={phoneNumber} pattern='[0-9]' onChange={(i) => setPhonenNumber(i.target.value)} required type="number" name="number" id="" />
                                    <label for="number">Phone number</label>
                                </div>
                                <div className="inputbox">
                                    <input onChange={(i) => setEmail(i.target.value)} required type="email" name="email" id="" />
                                    <label for="email">Email</label>
                                </div>
                                <div className="inputbox">
                                    <input onChange={(i) => setPassword(i.target.value)} required type="password" name="password" id="" />
                                    <label for="password">Password</label>
                                </div>
                                <div className="forget">
                                    <label>Forgot password? <a>New password</a></label>
                                </div>
                                <button type='submit'>Log in</button>
                                <div className="register">
                                    <p>No account? <NavLink onClick={() => delayLink("/login")} className={`${({ isActive, isPending }) => isPending ? "pending" : isActive ? "active" : ""} a`}>Log in</NavLink></p>
                                </div>
                            </form>
                        </div>
                    </div>
                </section>
            </div>
            </>}
        
        </div>
    )
}
