import React, {useState, useEffect} from 'react'
import { useNavigate } from 'react-router-dom'
import "./Register.scss"
import BacdropImage from '../../Assets/Images/photo_2023-10-19_07-28-40.jpg'
import { NavLink } from 'react-router-dom'
export default function Register({ to, delay, children, ...rest }) {
    const navigate = useNavigate();
    const [isLoading, setIsLoading] = useState(false);
    const postLoginData = async()=>{

    }
    useEffect(() => {
        const delay = 3000; 
        const redirectTimeout = setTimeout(() => {
            setIsLoading(false);
            // Перенаправление на другую страницу
          }, delay);
      
          return () => {
            clearTimeout(redirectTimeout); // Очистка таймера при размонтировании компонента
          };
      }, [navigate]);
      if (isLoading) {
        return (
          <div>
            {/* Загрузочный компонент или индикатор загрузки */}
            Загрузка...
          </div>
        );
      }

  return (
    <div>
       <img className='login-image' src={BacdropImage} alt="LoginImage" />
      <div>
        <section>
            <div className="form-box">
                <div className="form-value">
                    <form onSubmit={postLoginData}>
                        <h2>Login</h2>
                        <div className="inputbox">
                            <input required type="email" name="number" id="" />
                            <label for="number">Phone number</label>
                        </div>
                        <div className="inputbox">
                            <input required type="email" name="email" id="" />
                            <label for="email">Email</label>
                        </div>
                        <div className="inputbox">
                            <input required type="password" name="password" id="" />
                            <label for="password">Password</label>
                        </div>
                        <div className="forget">
                        <label>Forgot password? <a>New password</a></label>
                        </div>
                        <button type='submit'>Log in</button>
                        <div className="register">
                            <p>No account? <NavLink to='/login' className={`${({ isActive, isPending }) => isPending ? "pending" : isActive ? "active" : ""} a`}>Log in</NavLink></p>
                        </div>
                    </form>
                </div>
            </div>
        </section>
      </div>
    </div>
  )
}
