import React, { useState, useRef, useEffect } from 'react';
import "./Card.scss"
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Data from '../../Utils/Data';
const Card = (props) => {
    const notify = () => toast.success('Wow so easy!');
    const [foods, setFoods] = useState([])
    const [categoriy, setCategoriy] = useState([])
    useEffect(() => {
        Data.GetFoods()
            .then(response => {
                setFoods(response)
            })
        Data.GetCategoriy()
            .then(response => {
                response.map(iteam => {
                    setFoods(iteam.food)
                })
                setCategoriy(response)
            })
    }, [])
    return (
        <div>
            <ToastContainer
                position="top-center"
                autoClose={1}
                hideProgressBar={false}
                newestOnTop={false}
                closeOnClick
                rtl={false}
                pauseOnFocusLoss={false}
                draggable
                pauseOnHover={false}
                theme="dark" />
            <div className='card-row'>
                {foods?.length > 0 && foods?.map((iteam, index) => {
                    return (
                        <div className='food-card'>
                            <div style={{ backgroundImage: `url(${iteam.imageUrl})` }} className='card-img'>
                            </div>
                            <div className='card-title'>
                                <h5>{iteam.name}</h5>
                                <p>$ 2.29</p>
                                <p>{iteam.count} Bowls available</p>
                            </div>
                            <div className='card-buy'>
                                <button onClick={notify}>Add</button>
                            </div>
                        </div>
                    )
                })}
            </div>

        </div>
    );
};

export default Card;
