const userFoods = []

function addFoods(state = userFoods, action) {
    const product = action.payload;
    switch (action.type) {
        case "addFood":
            const list = state.find(i => i.id === product.id);
            if (list) {
                return state.map(i => i.id === product.id ? { ...i, count: i.count + 1 } : i)
            }
            else {
                const product = action.payload;
                return [
                    ...state,
                    {
                        ...product,
                        count: 1,
                    }
                ]
            }
        default: return state

    }

}
export { addFoods }
