import React, { useEffect, useState } from 'react'
import { NavLink } from 'react-router-dom'
import { useNavigate } from 'react-router-dom'
import Delay from '../Delay/Delay';
export default function SiteBar() {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(true)
  const [sitebar, setSitebar] = useState([
    {
      icon: "bi bi-list",
      id: 1,
      path: "/history",
    },
    {
      icon: "bi bi-house",
      id: 2,
      path: "/home",
    },
    {
      icon: "bi bi-gear",
      id: 3,
      path: "/setting",
    },
    {
      icon: "bi bi-pie-chart",
      id: 4,
      path: "/piechard",
    },
    {
      icon: "bi bi-chat-square",
      id: 5,
      path: "/chat",
    },
    {
      icon: "bi bi-cart3",
      id: 5,
      path: "/shop",
    },
    {
      icon: "bi bi-person",
      id: 5,
      path: "/login",
    },
  ])
  function delay(page) {
    setIsLoading(false)
    const delay = 1000;
    <Delay />
    const redirectTimeout = setTimeout(() => {
      setIsLoading(true)
      navigate(page);
    }, delay);

    return () => {
      clearTimeout(redirectTimeout);
    };
  }

  return (
    <div>
      {!isLoading ? <Delay /> : <div className='row '>
        {
          sitebar?.map((iteam, index) => {
            return (
              <div key={index} className="col-12">
                <div>
                  <NavLink onClick={() => delay(iteam.path)} className={`${({ isActive, isPending }) => isPending ? "pending" : isActive ? "active" : ""} `}>
                    <i className={iteam.icon}></i>
                  </NavLink>
                </div>
              </div>
            )
          })
        }
      </div>}

    </div>
  )
}
